import GameBoard from "./GameBoard";

const generateLines = (word) => {

    const highestColumn = word.length;
    const numberOfRows = 9;

    let rows = new Array();
    for (var i = 0; i < numberOfRows; i++) {
        const column = Math.floor(Math.random() * highestColumn) + 1;
        rows.push({ column });
    }
    return rows;
};


export default function Game() {

    const { scrambleWord } = useScrambler();

    // const word = "anagramida";
    const word = "cat"
    const scrambledWord = scrambleWord(word);

    return (
        <GameBoard word={scrambled} initialLines={generateLines(word)} />
    );
}
