import styled from "styled-components";
import WordDisplay from "./WordDisplay";
import { useState } from "react";

const swapLetters = (word, leftSwapLetterPosition) => {
    const beforeLetters =
        leftSwapLetterPosition > 0 ?
            word.slice(0, leftSwapLetterPosition) :
            "";
    const leftLetter = word.charAt(leftSwapLetterPosition);
    const rightLetter = word.charAt(leftSwapLetterPosition + 1);
    const afterLetters = word.slice(leftSwapLetterPosition + 2);
    return `${beforeLetters}${rightLetter}${leftLetter}${afterLetters}`;
};

const scrambleWord = (word, lines) => {
    let scrambledWord = `${word}`;

    lines.forEach(element => {
        if (element.column) {
            scrambledWord = swapLetters(scrambledWord, element.column - 1);
        }
    });

    return scrambledWord;
};

const LineRow = styled.div`
    height: 16px;
    width: 100%;
    border: none;
    display: flex;
    justify-content: space-between;
`;

const DownwardLine = styled.div`
    width: 16px;
    height: 16px;
    background-color: gray;
    border-bottom: none;
`;

const LineContainer = styled.div`
    width: 100%;
    height: 16px;
    background-color: white;
    border-bottom: none;
`;

const Line = styled.div`
    width: 100%;
    height: 2px;
    background-color: black;
`;

const GridDisplay = styled.div`
    padding: 0 8px 0 8px;
`;

const ButtonDisplay = styled.div`
    display: flex;
    justify-content: space-around;
`;

export default function GameBoard({ word, initialLines }) {

    const [lines, setLines] = useState(initialLines);
    const scrambledWord = scrambleWord(word, lines);

    const handleButtonClicked = (index) => {
        setLines([{ column: index }, ...lines]);
    }

    const getButtons = (numberOfButtons) => {
        let buttons = [];
        for (var i = 0; i < numberOfButtons; i++) {
            const buttonIndex = i + 1;
            buttons.push(
                <button
                    key={`button-${buttonIndex}`}
                    data-testid={`button-${buttonIndex}`}
                    onClick={() => handleButtonClicked(buttonIndex)}
                >
                    v
            </button>
            );
        }
        return buttons;
    }

    return (
        <>
            <WordDisplay word={word} />
            <ButtonDisplay>
                {getButtons(word.length - 1)}
            </ButtonDisplay>
            <GridDisplay>
                {lines.map((line, index) => (
                    <LineRow key={`line-row-${index}`} data-testid={`line-row-${index + 1}`}>
                        {[...word].map((_, index) => (
                            <Fragment key={index}>
                                <DownwardLine />
                                {index < word.length - 1 &&
                                    (index === (line.column - 1) ?
                                        <Line /> :
                                        <LineContainer />)
                                }
                            </Fragment>
                        ))}
                    </LineRow>
                ))}
            </GridDisplay>
            <WordDisplay word={scrambledWord} />
        </>
    );
}
