import styled from "styled-components";
import WordDisplay from "./WordDisplay";

const swapLetters = (word, leftSwapLetterPosition) => {
    const beforeLetters =
        leftSwapLetterPosition > 0 ?
            word.slice(0, leftSwapLetterPosition) :
            "";
    const leftLetter = word.charAt(leftSwapLetterPosition);
    const rightLetter = word.charAt(leftSwapLetterPosition + 1);
    const afterLetters = word.slice(leftSwapLetterPosition + 2);
    return `${beforeLetters}${rightLetter}${leftLetter}${afterLetters}`;
};

const scrambleWord = (word, lines) => {
    let scrambledWord = `${word}`;

    lines.forEach(element => {
        if (element.column) {
            scrambledWord = swapLetters(scrambledWord, element.column - 1);
        }
    });

    return scrambledWord;
};

const LineRow = styled.div`
    height: 16px;
    width: 100%;
    border: none;
    display: flex;
    justify-content: space-between;
`;

const DownwardLine = styled.div`
    width: 16px;
    height: 16px;
    background-color: gray;
    border-bottom: none;
`;

const LineContainer = styled.div`
    width: 100%;
    height: 16px;
    background-color: white;
    border-bottom: none;
`;

const Line = styled.div`
    width: 100%;
    height: 2px;
    background-color: black;
`;

const GridDisplay = styled.div`
    padding: 0 8px 0 8px;
`;

const ButtonDisplay = styled.div`
    display: flex;
    justify-content: space-around;
`;

const getButtons = (numberOfButtons) => {
    let buttons = [];
    buttons.push(<button>v</button>);
    for (var i; i < numberOfButtons; i++) {
        buttons.push(<button>v</button>);
    }
    return buttons;
    return (
        <>
            {buttons}
        </>
    );
}

export default function GameBoard({ word, lines }) {

    const scrambledWord = scrambleWord(word, lines);
    console.log(lines);

    return (
        <>
            <WordDisplay word={word} />
            <ButtonDisplay>
                {getButtons(word.length)}
            </ButtonDisplay>
            <GridDisplay>
                {lines.map((line, index) => (
                    <LineRow key={index} data-testid={`line-row-${index + 1}`}>
                        {[...word].map((_, index) => (
                            <>
                                <DownwardLine />
                                {index < word.length - 1 &&
                                    (index === (line.column - 1) ?
                                        <Line /> :
                                        <LineContainer />)
                                }
                            </>
                        ))}
                    </LineRow>
                ))}
            </GridDisplay>
            <WordDisplay word={scrambledWord} />
        </>
    );
}
